import React, { useEffect, useState, useContext } from "react";
import { userContext } from "../../App";

const Home = () => {
    return (
        <div class="home-container">
            <h1><em>Common Cents</em></h1>
            <p>Financial freedom</p>
            <div class="mission">
                <p>
                    We are a tech-enabled nonprofit empowering students with the knowledge
                    to manage their money and reach financial independence
                </p>
            </div>
            <h2>
                <u>Our Impact</u>
            </h2>
            <div class="impact-container">
                <Impact number="20" text="Members" />
                <Impact number="30" text="Chapters" />
                <Impact number="40" text="Students Taught" />
            </div>
            <div class="footnote-container">
                <p>
                    *Learn more about our purpose and impact <a href="#"><u>here</u></a>
                </p>
            </div>

            <h2>
                <u> Our Partners </u>
            </h2>
            <div class="carousel-container">
                <a class="arrow" onclick="plusSlides(-1)">
                    &lt;
                </a>
                <div class="impact-container">
                    <Partner text="Spark" />
                    <Partner text="Google" />
                    <Partner text="FB" />
                </div>{" "}
                <a class="arrow" onclick="plusSlides(1)">
                    &gt;
                </a>
            </div>
        </div>
    );
};

function Impact(props) {
    const number = props.number;
    const text = props.text;
    return (
        <div class="impact-box">
            <p class="number"> {number} </p>
            <p class="text"> {text} </p>
        </div>
    );
}

function Partner(props) {
    const text = props.text;
    return (
        <div class="partner-box">
            <p class="text"> {text} </p>
        </div>
    );
}

